# -*- coding: utf-8 -*-

import models

def get_page(page_name):
    try:
        page = models.Page.objects.get(slug=page_name)
        return {'title': page.title,
                'content': page.content,
                'header_content': page.header_content
                }
    except:
        return None
    
def get_common_content():
    try:
        about = models.Page.objects.get(slug='about_at_home')
        contacts = models.Page.objects.get(slug='contacts_at_home')
        return {'about_at_home': about.content,
                'contacts_at_home': contacts.content,
                }
    except:
        return {}
    
